import React from 'react'
import { FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import { faGithub, faLinkedin, faInstagram, faGear} from '@fortawesome/free-brands-svg-icons'
import sideButton from '../../assets/sideButton.png'
import writeButton from '../../assets/writeButton.png'
import './Sidebar.css'



const Sidebar = ({ setValue, setMessage, setCurrentTitle, uniqueTitles, isSidebarOpen, onToggle}) => {
  const toggleSidebar = () => {
    onToggle();
  }


  const createNewChat = () => {
    setMessage(null) 
    setValue("")
    setCurrentTitle(null)
  };

  const handleClick = (uniqueTitle) => {
    setCurrentTitle(uniqueTitle)
    setMessage(null) 
    setValue("")
  }

  const handleRename = (oldTitle) => {
    const newTitle = prompt("Rename", oldTitle);
    if (newTitle && newTitle !== oldTitle) {

    }
  };

  const handleDelete = (title) => {
    if (window.confirm(`Delete ${title}`)){
      deleteChat(title);
    }
  }

  return (
    <>

      <div className='sidebar-header'>
        <button 
          className='toggleButton' 
          onClick={toggleSidebar}
        >
          <img src={sideButton} alt='toggle icon' className='toggleIcon'/>
        </button>
        <button className='chatBtn' onClick={createNewChat}> 
            <img src={writeButton} alt='toggle icon'className='toggleIcon'/>
        </button>
      </div>

      <section className={`sidebar-container ${isSidebarOpen ? 'open' : 'closed'}`}>
        <div className='sideBar'>
          <div className='upperSide'>
              <div className='upperSideTop'>
              <ul className='history'>
                {uniqueTitles?.map((uniqueTitle, index) => (
                  <li key={index}>
                    <span onClick={() => handleClick(uniqueTitle)}>{uniqueTitle}</span>
                    <FontAwesomeIcon
                      icon={faGear}
                      className='editIcon'
                      onClick={() => {
                        const option = prompt('Choose an option: rename or delete');
                        if (option === 'rename') {
                          handleRename(uniqueTitle);
                        } else if (option === 'delete') {
                          handleDelete(uniqueTitle);
                        }
                      }}
                    />
                  </li>
                ))}
              </ul>
                  {/* <ul className="history">
                    {uniqueTitles?.map((uniqueTitle, index) => <li key={index} onClick={() => handleClick(uniqueTitle)}>{uniqueTitle}</li>)}
                  </ul> */}
              </div>
          </div>
          <div className='lowerSide'>
            <div className='listItems'>
              <FontAwesomeIcon icon={faGithub} className='listitemsImg'/>
              GitHub 
            </div>
            <div className='listItems'>
              <FontAwesomeIcon icon={faLinkedin} className='listitemsImg'/>
              Linkedin
            </div>
            <div className='listItems'>
              <FontAwesomeIcon icon={faInstagram}className='listitemsImg'/>
              Instagram
            </div>
          </div>
        </div>
      </section>

    </>
  )
}

export default Sidebar
