import React, { useState, useEffect } from 'react';
import './Chats.css';
import sendBtn3 from '../../assets/sendBtn3.svg';
import attachmentBtn from '../../assets/attachmentBtn.svg';

const Chats = ({ value, setValue, message, setMessage, currentTitle, setCurrentTitle, setUniqueTitles}) => {
  const [previousChats, setPreviousChats] = useState([]);

  const getMessages = async () => {
    const options = {
      method: "POST",
      body: JSON.stringify({ message: value }),
      headers: { "Content-Type": "application/json" }
    };
    try {
      const response = await fetch('http://localhost:8000/completions', options);
      const data = await response.json();
      setMessage(data.choices[0].message);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    if (value && message) {
      if (!currentTitle) {
        setCurrentTitle(value);
      }
      setPreviousChats(prevChats => {
        const newChats = [
          ...prevChats,
          { title: currentTitle || value, role: "user", content: value },
          { title: currentTitle || value, role: message.role, content: message.content }
        ];
        const uniqueTitles = Array.from(new Set(newChats.map(chat => chat.title)));
        setUniqueTitles(uniqueTitles);
        return newChats;
      });
    }
  }, [message]);

  const currentChat = previousChats.filter(chat => chat.title === currentTitle);



  return (
    <div className='chat-container'>
      <ul className='feed'>
        {currentChat.map((chatMessage, index) => (
          <li key={index}>
            <p className='role'>{chatMessage.role}</p>
            <p>{chatMessage.content}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Chats;
