import React, { useState, useCallback } from 'react';
import { Sidebar, Header, Chats } from './components/index';
import './App.css';

const App = () => {
  const [value, setValue] = useState('');
  const [message, setMessage] = useState(null);
  const [currentTitle, setCurrentTitle] = useState(null);
  const [uniqueTitles, setUniqueTitles] = useState([]);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isTyping, setIsTyping] = useState(false);

  const handleSidebarToggle = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const memoizedSetCurrentTitle = useCallback((title) => {
    setCurrentTitle(title);
  }, []);

  const memoizedSetUniqueTitles = useCallback((titles) => {
    setUniqueTitles(titles);
  }, []);

  return (
    <div className={`App ${!isSidebarOpen ? 'sidebar-closed' : ''}`}>
      <Sidebar
        setValue={setValue}
        setMessage={setMessage}
        setCurrentTitle={memoizedSetCurrentTitle} 
        uniqueTitles={uniqueTitles}
        isSidebarOpen={isSidebarOpen}
        onToggle={handleSidebarToggle}
      />
      <div className={`main ${!isSidebarOpen ? 'centered' : ''}`}>
        <Header />
        <Chats
          value={value}
          setValue={setValue}
          message={message}
          setMessage={setMessage}
          currentTitle={currentTitle}
          setCurrentTitle={memoizedSetCurrentTitle} 
          setUniqueTitles={memoizedSetUniqueTitles} 
          isTyping={isTyping}
          setIsTyping={setIsTyping}
        />
      </div>
    </div>
  );
}

export default App;
