import React, {useState, useEffect} from 'react'
import './Chats.css'
import sendBtn3 from '../../assets/sendBtn3.svg'
import attachmentBtn from '../../assets/attachmentBtn.svg'

const Chats = ({value, setValue, message, setMessage, currentTitle, setCurrentTitle}) => {

  const [ previousChats, setPreviousChats ] = useState([])


  const getMessages = async () => {
    const options = {
      method: "POST",
      body: JSON.stringify({
        message: value
      }),
      headers: {
        "Content-Type": "application/json"
      }
    }
    try {
      const response = await fetch('http://localhost:8000/completions', options)

      const data = await response.json()
      setMessage(data.choices[0].message)

    } catch (error) {
      console.error(error)
    }
  }

  useEffect(() => {
    console.log(currentTitle, value, message)
    if (!currentTitle && value && message) {
      setCurrentTitle(value)
    }
    if(currentTitle && value && message) {
      setPreviousChats(prevChats => (
        [...prevChats, 
          {
            title: currentTitle,
            role: "user",
            content: value
          },
          {
            title: currentTitle,
            role: message.role,
            content: message.content
          }
        ]
      ))
    }
  },[message, currentTitle])

  console.log(previousChats)

  const currentChat = previousChats.filter(previousChats => previousChats.title === currentTitle)

  const uniqueTitles = Array.from(new Set(previousChats.map(previousChats => previousChats.title)))

  console.log(uniqueTitles)

  return (
    <>
      <ul className='feed'>
        {currentChat?.map
        ((chatMessage, index) => 
        <li key={index}></li>)}
          <p className='role'>{chatMessage.role}</p>
          <p className='user'>{chatMessage.message}</p>
      </ul>
      <div className='chatFooter'>
        <div className = "input-container">
          <div className='inp'>
              <input 
              value ={value}
              onChange ={(e) => setValue(e.target.value)}
              type ='text' 
              placeholder ='Message Chatbot...'
              />
              <button className='send' onClick={getMessages}>
                  <img src={sendBtn3} alt='send'className='sendBtn' id='submit'/>
              </button>
              <button className='attachment'>
                  <img src={attachmentBtn} alt='send'className='attachmentBtn'/>
              </button>
          </div>
          <p className='info'>
            ChatBot Mar 14 Version. Free Research Preview. Our Goal is to make AI systems more natural and safe to interact with. Your feedback will help us improve. 
          </p>
        </div>
      </div>
    </>
  )
}

export default Chats
